import React, { useEffect, useState } from "react";
import "./Home.css";
import "../header/header";
import { Link } from "react-router-dom";
import "../header/header";
import KH from "../KhoaHoc/Khoahoc";
export default function Home() {
  // tim kiem
  const [courses, setCourses] = useState([]);
  const [query, setQuery] = useState("");
  useEffect(() => {
    fetch("http://localhost:3000/api/courses")
      .then((response) => response.json())
      .then((data) => setCourses(data));
  }, []);

  const deleteCourse = async (id) => {
    await fetch(`http://localhost:3000/api/courses/${id}`, {
      // xoa tren bback end
      method: "DELETE",
    });
    await fetch("http://localhost:3000/api/courses")
      // xoa tren front end. Dong bo front - back
      .then((response) => response.json())
      .then((data) => setCourses(data));
  };

  //lấy cái đã nhập --->Tìm kiếm
  const onChangeFilter = (e) => {
    setQuery(e.target.value);
  };

  return (
    <div style={{ marginTop: "5em" }}>
      <div>
        {/* ======= Hero Section ======= */}
        <section
          id="hero"
          className="d-flex justify-content-center align-items-center"
        >
          <div
            className="container position-relative"
            data-aos="zoom-in"
            data-aos-delay={100}
          >
            <h1>
              Học Hôm Nay
              <br />
              Dẫn Đầu ngày Mai
            </h1>
            <h2>Cùng Blue Sky đi đến những chân trời mới!</h2>
            <a
              className="btn "
              style={{
                backgroundColor: "#3685c5",
                color: "white",
                marginTop: "3em",
              }}
            >
              <Link style={{ color: "white" }} to="/dang-nhap">
                {" "}
                Đăng nhập
              </Link>
            </a>
          </div>
        </section>
        {/* End Hero */}
        <main id="main">
          {/* Search */}
          <div
            className="container"
            style={{
              marginTop: "5em",
              margin: "auto",
            }}
          >
            <input
              style={{
                marginTop: "5em",
                maxWidth: "300px",
                maxHeight: "50px",
                width: "100%",
                height: "100%",
                // margin:"auto",
              }}
              type="text"
              name="search"
              onChange={(e) => {
                onChangeFilter(e);
              }}
              placeholder="Nhập mã code của lớp học"
            ></input>
            <button
              style={{ height: "50px", color: "black" }}
              className="btn btn-info"
              onClick={() => {
                alert("Hiện ra lớp học");
              }}
            >
              {" "}
              Tìm lớp
            </button>
          </div>
          {/* ======= Popular Courses Section ======= */}
          <section
            id="popular-courses"
            className="courses"
            style={{ marginTop: "2em" }}
          >
            <div className="container" data-aos="fade-up">
              <div className="section-title">
                <h2>Events</h2>
                <p>Events</p>
              </div>
              <div className="row" data-aos="zoom-in" data-aos-delay={100}>
                {courses.map((kh) => {
                  if (query === kh.MaCode)
                    return (
                      <KH
                        key={kh.id}
                        tenGv={kh.teacher}
                        tenKh={kh.name}
                        soBai={kh.Sobai}
                        maLop={kh.MaCode}
                        avt={kh.avt}
                        imgCourses={kh.imgCourses}
                        id={kh.id}
                        deleteCourse={deleteCourse}
                      />
                    );
                })}
              </div>
            </div>
          </section>
          {/* End Popular Courses Section */}
        </main>
        {/* End #main */}
        {/* ======= Events Section ======= */}
        <section id="events" className="events">
          <div className="container" data-aos="fade-up">
            <div className="row">
              <div className="col-md-6 d-flex align-items-stretch">
                <div className="card">
                  <div className="card-img">
                    <img
                      src="https://vtv1.mediacdn.vn/thumb_w/650/2019/12/8/photo-1-1575771579707516128571.jpg"
                      alt="..."
                    />
                  </div>
                  <div className="card-body">
                    <h5 className="card-title">
                      <a href>Introduction to webdesign</a>
                    </h5>
                    <p className="font-italic text-center">
                      Sunday, September 26th at 7:00 pm
                    </p>
                    <p className="card-text">
                      Are there any requirements for participation? You have to
                      be present at the venue for the full Hackathon event,
                      which will take place in HCMC, Vietnam (see Regulations
                      for full rules set). Other than that, all AI enthusiasts,
                      engineers, and students are welcome to join!
                    </p>
                  </div>
                </div>
              </div>
              <div className="col-md-6 d-flex align-items-stretch">
                <div className="card">
                  <div className="card-img">
                    <img src="assets/img/events-2.jpg" alt="..." />
                  </div>
                  <div className="card-body">
                    <h5 className="card-title">
                      <a href>Marketing Strategies</a>
                    </h5>
                    <p className="font-italic text-center">
                      Sunday, November 15th at 7:00 pm
                    </p>
                    <p className="card-text">
                      Sed ut perspiciatis unde omnis iste natus error sit
                      voluptatem doloremque laudantium, totam rem aperiam, eaque
                      ipsa quae ab illo inventore veritatis et quasi architecto
                      beatae vitae dicta sunt explicabo
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
        {/* End Events Section */}
        {/* ======= Events Section ======= */}
        <section id="events" className="events">
          <div className="container" data-aos="fade-up">
            <div className="row">
              <div className="col-md-6 d-flex align-items-stretch">
                <div className="card">
                  <div className="card-img">
                    <img
                      src="https://www.sxsw.com/wp-content/uploads/2019/06/2019-Hackathon-Photo-by-Randy-and-Jackie-Smith.jpg"
                      alt="..."
                    />
                  </div>
                  <div className="card-body">
                    <h5 className="card-title">
                      <a href>Introduction to webdesign</a>
                    </h5>
                    <p className="font-italic text-center">
