import React, { useEffect, useState } from "react";
import "./Home.css";
import "../header/header";
import { Link } from "react-router-dom";
import "../header/header";
import KH from "../KhoaHoc/Khoahoc";
export default function Home() {
  const [courses, setCourses] = useState([]);
  const [query, setQuery] = useState("");
  useEffect(() => {
    fetch("http://localhost:3000/api/courses")
      .then((response) => response.json())
      .then((data) => setCourses(data));
  }, []);

  const deleteCourse = () => {
    fetch(`http://localhost:3000/api/courses/${deleteid}`, {
      method: "DELETE",
    })
      .then((response) => response.json())
      .then((data) => setCourses(data));
  };

  // useEffect(() => {
  //   console.log(courses[0]);
  // }, [courses]);
  const onChangeFilter = (e) => {
    setQuery(e.target.value);
  };

  return (
    <div style={{ marginTop: "5em" }}>
      <div>
        {/* ======= Hero Section ======= */}
        <section
          id="hero"
          className="d-flex justify-content-center align-items-center"
        >
          <div
            className="container position-relative"
            data-aos="zoom-in"
            data-aos-delay={100}
          >
            <h1>
              Học Hôm Nay
              <br />
              Dẫn Đầu ngày Mai
            </h1>
            <h2>Cùng Blue Sky đi đến những chân trời mới!</h2>
            <a
              className="btn "
              style={{
                backgroundColor: "#3685c5",
                color: "white",
                marginTop: "3em",
              }}
            >
              <Link style={{ color: "white" }} to="/dang-nhap">
                {" "}
                Đăng nhập
              </Link>
            </a>
          </div>
        </section>
        {/* End Hero */}
        <main id="main">
          {/* Search */}
          <div
            className="container"
            style={{
              marginTop: "5em",
              margin: "auto",
            }}
          >
            <input
              style={{
                marginTop: "5em",
                maxWidth: "300px",
                maxHeight: "50px",
                width: "100%",
                height: "100%",
                // margin:"auto",
              }}
              type="text"
              name="search"
              onChange={(e) => {
                onChangeFilter(e);
              }}
              placeholder="Nhập mã code của lớp học"
            ></input>
            <button
              style={{ height: "50px", color: "black" }}
              className="btn btn-info"
              onClick={() => {
                alert("Hiện ra lớp học");
              }}
            >
              {" "}
              Tìm lớp
            </button>
          </div>
          {/* ======= Popular Courses Section ======= */}
          <section
            id="popular-courses"
            className="courses"
            style={{ marginTop: "2em" }}
          >
            <div className="container" data-aos="fade-up">
              <div className="section-title">
                <h2>Top Courses</h2>
                <p>Top Courses</p>
              </div>
              <div className="row" data-aos="zoom-in" data-aos-delay={100}>
                {courses.map((kh) => {
                  if (query === kh.MaCode)
                    return (
                      <KH
                        tenGv={kh.teacher}
                        tenKh={kh.name}
                        soBai={kh.Sobai}
                        maLop={kh.MaCode}
                        avt={kh.avt}
                        imgCourses={kh.imgCourses}
                      />
                    );
                })}
              </div>
            </div>
          </section>
          {/* End Popular Courses Section */}
        </main>
        {/* End #main */}
      </div>
    </div>
  );
}
