import React, { useEffect, useState } from "react";
import "./Home.css";
import "../header/header";
import { Link } from "react-router-dom";
import "../header/header";
import KH from "../KhoaHoc/Khoahoc";
export default function Home() {
  // tim kiem
  const [courses, setCourses] = useState([]);
  const [query, setQuery] = useState("");
  useEffect(() => {
    fetch("http://localhost:3000/api/courses")
      .then((response) => response.json())
      .then((data) => setCourses(data));
  }, []);

  const deleteCourse = async (id) => {
    await fetch(`http://localhost:3000/api/courses/${id}`, {
      // xoa tren bback end
      method: "DELETE",
    });
    await fetch("http://localhost:3000/api/courses")
      // xoa tren front end. Dong bo front - back
      .then((response) => response.json())
      .then((data) => setCourses(data));
  };

  //lấy cái đã nhập --->Tìm kiếm
  const onChangeFilter = (e) => {
    setQuery(e.target.value);
  };

  return (
    <div style={{ marginTop: "5em" }}>
      <div>
        {/* ======= Hero Section ======= */}
        <section
          id="hero"
          className="d-flex justify-content-center align-items-center"
        >
          <div
            className="container position-relative"
            data-aos="zoom-in"
            data-aos-delay={100}
          >
            <h1>
              Học Hôm Nay
              <br />
              Dẫn Đầu ngày Mai
            </h1>
            <h2>Cùng Blue Sky đi đến những chân trời mới!</h2>
            <a
              className="btn "
              style={{
                backgroundColor: "#3685c5",
                color: "white",
                marginTop: "3em",
              }}
            >
              <Link style={{ color: "white" }} to="/dang-nhap">
                {" "}
                Đăng nhập
              </Link>
            </a>
          </div>
        </section>
        {/* End Hero */}
        <main id="main">
          {/* Search */}
          <div
            className="container"
            style={{
              marginTop: "5em",
              margin: "auto",
            }}
          >
            <input
              style={{
                marginTop: "5em",
                maxWidth: "300px",
                maxHeight: "50px",
                width: "100%",
                height: "100%",
                // margin:"auto",
              }}
              type="text"
              name="search"
              onChange={(e) => {
                onChangeFilter(e);
              }}
              placeholder="Nhập mã code của lớp học"
            ></input>
            <button
              style={{ height: "50px", color: "black" }}
              className="btn btn-info"
              onClick={() => {
                alert("Hiện ra lớp học");
              }}
            >
              {" "}
              Tìm lớp
            </button>
          </div>
          {/* ======= Popular Courses Section ======= */}
          <section
            id="popular-courses"
            className="courses"
            style={{ marginTop: "2em" }}
          >
            <div className="container" data-aos="fade-up">
              <div className="section-title">
                <h2>Top Courses</h2>
                <p>Top Courses</p>
              </div>
              <div className="row" data-aos="zoom-in" data-aos-delay={100}>
                {courses.map((kh) => {
                  if (query === kh.MaCode)
                    return (
                      <KH
                        key={kh.id}
                        tenGv={kh.teacher}
                        tenKh={kh.name}
                        soBai={kh.Sobai}
                        maLop={kh.MaCode}
                        avt={kh.avt}
                        imgCourses={kh.imgCourses}
                        id={kh.id}
                        deleteCourse={deleteCourse}
                      />
                    );
                })}
              </div>
            </div>
          </section>
          {/* End Popular Courses Section */}
        </main>
        {/* End #main */}
        {/* ======= Testimonials Section ======= */}
        <section id="testimonials" className="testimonials">
          <div className="container" data-aos="fade-up">
            <div className="section-title">
                <h2>Testimonials</h2>
                <p>What are they saying</p>
              </div>
    
            <div
              // className="owl-carousel testimonials-carousel"
              // data-aos="zoom-in"
              // data-aos-delay={100}
            >
              <div className="testimonial-wrap">
                <div className="testimonial-item">
                  <img
                    src="https://i.pinimg.com/736x/ea/4f/ce/ea4fce64304667817ec58184bed9eac1.jpg"
                    className="testimonial-img"
                    alt=""
                  />
                  <h3>Saul Goodman</h3>
                  <h4>Ceo &amp; Founder</h4>
                  <p>
                    <i className="bx bxs-quote-alt-left quote-icon-left" />
                    Proin iaculis purus consequat sem cure digni ssim donec
                    porttitora entum suscipit rhoncus. Accusantium quam,
                    ultricies eget id, aliquam eget nibh et. Maecen aliquam,
                    risus at semper.
                    <i className="bx bxs-quote-alt-right quote-icon-right" />
                  </p>
                </div>
              </div>
              <div className="testimonial-wrap">
                <div className="testimonial-item">
                  <img
                    src="https://i.pinimg.com/736x/ea/4f/ce/ea4fce64304667817ec58184bed9eac1.jpg"
                    className="testimonial-img"
